import React from 'react';
import { ResponsiveBar } from '@nivo/bar';
import './TopWords.css';

const TopWords = ({ wordData }) => {
    const sortedWordData = [...wordData].sort((a, b) => b.value - a.value);

    const data = sortedWordData.map(word => ({
        word: word.text,
        frequency: word.value
    }));

    return (
        <div className="top-words-chart">
            <ResponsiveBar
                data={data}
                keys={['frequency']}
                indexBy="word"
                margin={{ top: 50, right: 130, bottom: 50, left: 60 }}
                padding={0.3}
                layout="horizontal"
                valueScale={{ type: 'linear' }}
                indexScale={{ type: 'band', round: true }}
                colors={{ scheme: 'nivo' }}
                borderColor={{ from: 'color', modifiers: [['darker', 1.6]] }}
                axisTop={null}
                axisRight={null}
                axisBottom={{
                    tickSize: 5,
                    tickPadding: 5,
                    tickRotation: 0,
                    legend: 'frequency',
                    legendPosition: 'middle',
                    legendOffset: 32
                }}
                axisLeft={{ tickSize: 5, tickPadding: 5, tickRotation: 0, legend: 'word', legendPosition: 'middle', legendOffset: -40 }}
                labelSkipWidth={12}
                labelSkipHeight={12}
                labelTextColor={{ from: 'color', modifiers: [['darker', 1.6]] }}
                animate={true}
                motionStiffness={90}
                motionDamping={15}
            />
        </div>
    );
};

export default TopWords;
