import React, { useState } from 'react';
import './TextInputForm.css';
import { ReactComponent as UpArrowIcon } from './up-arrow-icon.svg';

const TextInputForm = ({ onAnalysisComplete, onTextChange }) => {
    const [text, setText] = useState('');
    const [isTextEntered, setIsTextEntered] = useState(false);

    const predefinedComments = {
        set1: 
            "Absolutely love this product, it's amazing!\n" +
            "Unfortunately, it didn't meet my expectations.\n" +
            "The product is okay, nothing special.\n" +
            "Some features are great, others not so much.\n" +
            "Enjoyed the design, but the functionality could be improved.\n" +
            "Customer support was helpful and prompt in their response.\n" +
            "Found it a bit overpriced for the quality provided.\n" +
            "Impressed with the speed, but the accuracy needs work.\n" +
            "Good value for money, but has some minor issues.\n" +
            "Fantastic service, always goes above and beyond!\n" +
            "It's functional, but I expected more features.",

        set2: 
            "This service is outstanding, exceeded my expectations!\n" +
            "Not very satisfied with the customer service.\n" +
            "Average performance, doesn’t stand out from competitors.\n" +
            "A mix of good and bad, hard to give a definitive opinion.\n" +
            "Loved the user interface, very intuitive and user-friendly.\n" +
            "The installation process was more complicated than expected.\n" +
            "Delivers consistent results, but lacks advanced features.\n" +
            "Has potential, but the current version lacks polish.\n" +
            "Incredible results, couldn’t be happier!\n" +
            "Functional, yet lacks innovative features.",

        set3: 
            "Really satisfied with the purchase, it’s a great product!\n" +
            "Disappointed with the durability, started having issues quickly.\n" +
            "An okay choice for everyday use, but nothing extraordinary.\n" +
            "Mixed feelings about the service, some aspects are excellent.\n" +
            "Very efficient and saves a lot of time.\n" +
            "The product is average, was expecting something more innovative.\n" +
            "Great concept, but the execution isn't there yet.\n" +
            "Quality is decent, but I’ve seen better in the market.\n" +
            "A lifesaver in many situations, highly recommend it!\n" +
            "Needs improvements in customer support and service.",

        set4: 
            "Exceeded all my expectations, a top-notch product!\n" +
            "The user interface is clunky and difficult to navigate.\n" +
            "For its price, the product offers reasonable quality.\n" +
            "The features are a hit and miss, some are great, some aren't.\n" +
            "Performance is reliable and consistent.\n" +
            "Could benefit from more frequent updates and bug fixes.\n" +
            "The product stands out for its innovative approach.\n" +
            "Lacks the robustness I was hoping for in a premium product.\n" +
            "Excellent choice for anyone looking for quality and reliability.\n" +
            "The setup process was more complex than necessary."
    };




    const handleTextChange = (e) => {
        const newText = e.target.value;
        setText(newText);
        onTextChange(newText);
        setIsTextEntered(newText.trim() !== '');
    };

    const handlePredefinedCommentClick = (comment) => {
        setText(comment);
        setIsTextEntered(true);
        onTextChange(comment);
    };

    const callAPI = async (inputText) => {
        try {
            const myHeaders = new Headers();
            myHeaders.append("Content-Type", "application/json");

            const raw = JSON.stringify({ "text": inputText });
            const requestOptions = {
                method: 'POST',
                headers: myHeaders,
                body: raw,
                redirect: 'follow'
            };

            const response = await fetch("https://eip9ikhrl0.execute-api.us-east-2.amazonaws.com/dev", requestOptions);
            const result = await response.json();
            return result.body;
        } catch (error) {
            console.error('error', error);
            return null;
        }
    }

    const handleSubmit = async (event) => {
        event.preventDefault();
        if (!isTextEntered) {
            return;
        }
        const comments = text.split('\n').filter(comment => comment.trim() !== '');
        const results = await Promise.all(comments.map(comment => callAPI(comment)));
        onAnalysisComplete(results);
    };

    return (
        <div className="text-input-form">
        {!isTextEntered && (
            <div className="predefined-comments-container">
                {Object.entries(predefinedComments).map(([set, comments], index) => (
                    <div key={set} className="predefined-comment-set">
                        <div className="predefined-comments-box">
                            {comments.split('\n').map((comment, commentIndex) => (
                                <button
                                    key={commentIndex}
                                    className="predefined-comment-button"
                                    onClick={() => handlePredefinedCommentClick(comment)}
                                >
                                    {comment}
                                </button>
                            ))}
                        </div>
                    </div>
                ))}
            </div>
        )}
            <form onSubmit={handleSubmit}>
                <textarea 
                    className="text-input" 
                    value={text} 
                    onChange={handleTextChange}
                    placeholder="Enter your texts (one comment per line)"
                />
                <button type="submit" className={`submit-button ${isTextEntered ? '' : 'inactive'}`}>
                    <UpArrowIcon />
                </button>
            </form>
        </div>
    );
};

export default TextInputForm;
