import React from 'react';
import { Pie } from 'react-chartjs-2';
import './SentimentPieChart.css'; // Importing the CSS for styling

const SentimentPieChart = ({ sentimentData }) => {
    const data = {
        labels: ['Positive', 'Negative', 'Neutral', 'Mixed'],
        datasets: [
            {
                label: 'Sentiment Distribution',
                data: sentimentData,
                backgroundColor: ['#4caf50', '#f44336', '#ffeb3b', '#9e9e9e'],
                borderWidth: 0, // No borders for a sleek look
                hoverOffset: 6, // Enhanced hover effect for better interaction
            },
        ],
    };

    const options = {
        responsive: true,
        maintainAspectRatio: false, // Ensure the chart adapts to container
        plugins: {
            legend: {
                position: 'bottom', // Positioned for optimal visibility
                labels: {
                    boxWidth: 20,
                    padding: 20,
                    font: {
                        size: 16, // Larger, clearer font
                        family: "'Helvetica Neue', 'Helvetica', 'Arial', sans-serif", // Modern font
                    },
                    color: '#333' // Dark color for contrast
                }
            },
            tooltip: {
                enabled: true,
                backgroundColor: '#333', // Dark tooltip for clarity
                titleFont: {
                    size: 18,
                    weight: 'bold',
                    family: "'Helvetica Neue', 'Helvetica', 'Arial', sans-serif"
                },
                bodyFont: {
                    size: 16,
                    family: "'Helvetica Neue', 'Helvetica', 'Arial', sans-serif"
                },
                padding: 15,
                cornerRadius: 4, // Slight rounding for modern feel
                caretSize: 7, // Adjust for better visibility
                bodySpacing: 5, // Space between lines
            }
        },
        cutout: '70%', // Create a doughnut-like appearance for a modern twist
        animation: {
            animateScale: true, // Scale animation for an engaging look
        }
    };

    return (
        <div className="sentiment-pie-chart">
            <Pie data={data} options={options} />
        </div>
    );
};

export default SentimentPieChart;
