// SentimentPieChart.jsx

import React from 'react';
import { Pie } from 'react-chartjs-2';
import './SentimentPieChart.css'; // Importing the CSS for styling

const SentimentPieChart = ({ sentimentData }) => {
    // Prepare the data for the pie chart
    const data = {
        labels: ['Positive', 'Negative', 'Neutral', 'Mixed'], // Include 'Mixed'
        datasets: [
            {
                label: 'Sentiment Distribution',
                data: sentimentData, // Array of sentiment values [positive, negative, neutral, mixed]
                backgroundColor: ['#4caf50', '#f44336', '#ffeb3b', '#9e9e9e'], // Colors for the segments, add color for 'Mixed'
                borderWidth: 1,
            },
        ],
    };

    return (
        <div className="sentiment-pie-chart">
            <Pie data={data} />
        </div>
    );
};

export default SentimentPieChart;
